mapper.select:
SELECT COUNT(*)
FROM TBL_MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_PWD = #{memberPwd}
WHERE MEMBER_ID = #{memberId}

UPDATE TBL_MEMBER
SET POINT = #{point}
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT MEMBER_ID
FROM TBL_MEMBER
WHERE EMAIL = #{email} AND NAME = #{name}
